document.addEventListener("DOMContentLoaded",()=>{const t=document.querySelector('.product-form__input[name="Size"], .product-form__variants[name="Size"], .variant-wrapper fieldset.variant-input-wrap[name="Size"]');const s=t?t.querySelectorAll(".variant-input label, .product-form__label"):[];if(!s.length)return;let i=false;t.style.display="none";t.style.visibility="hidden";const a=document.createElement("div");a.className="size-selector";s.forEach(e=>{e.dataset.originalSizes=e.innerText;if(/UK [\w\s\d\.\-\/]+ \| EU [\w\s\d\.\-\/]+ \| US [\w\s\d\.\-\/]+/.test(e.dataset.originalSizes)){i=true}});if(!i)return;const n={UK:1,EU:2,US:3};Object.keys(n).forEach(e=>{const t=document.createElement("span");t.textContent=e;t.setAttribute("value",e);t.classList.add("size-option");if(e==="UK")t.classList.add("selected");t.addEventListener("click",()=>updateSizeFormat(e,t));a.appendChild(t)});t.prepend(a);function updateSizeFormat(i,e){document.querySelectorAll(".size-selector span").forEach(e=>e.classList.remove("selected"));e.classList.add("selected");s.forEach(e=>{const t=e.dataset.originalSizes.match(/UK ([\w\s\d\.\-\/]+) \| EU ([\w\s\d\.\-\/]+) \| US ([\w\s\d\.\-\/]+)/);if(t){e.innerText=t[n[i]]||"N/A"}else{console.warn("No valid size format found:",e.dataset.originalSizes)}});t.style.display="block";t.style.visibility="visible"}updateSizeFormat("UK",document.querySelector('.size-selector span[value="UK"]'));console.log("Functionality developed by Alliyan Gul @ishopifyexpert")});
